import React from 'react';

const Tooltip = ({ text }) => {
  return (
    <div className="relative inline-block group">
      <button className="text-purple-600 group-hover:text-purple-800 focus:outline-none">
        {/* Icon or text that triggers the tooltip */}
        Hover me
      </button>
      <div className="opacity-0 invisible group-hover:opacity-100 group-hover:visible absolute z-10 w-36 bg-white text-black p-2 rounded-lg shadow-lg">
        {text}
      </div>
    </div>
  );
};

export default Tooltip;
