import React from 'react'


function Card({ name, ilink, desc, link, trivia }) {
  return (
    <div className="relative">
      <a
        href={link}
        target="_blank"
        rel="noopener noreferrer"
        className="border-2 border-purple-900 rounded-md overflow-hidden shadow-lg hover:shadow-xl transition-transform transform hover:scale-105"
      >
        <img
          src={ilink}
          alt="preview-picture"
          className="w-full h-36 md:h-48 object-cover cursor-pointer"
        />
        <div className="w-full p-4">
          <h3 className="text-lg md:text-xl mb-2 md:mb-3 font-semibold">
            {name}
          </h3>
          <p className="flex flex-wrap gap-2 flex-row items-center justify-start text-xs md:text-sm">
            {desc}
          </p>
        </div>
      </a>
      <div className="absolute top-full left-0 right-0 bottom-0 opacity-0 bg-white text-black p-2 rounded-lg shadow-lg group-hover:opacity-100">
        {trivia}
      </div>
    </div>
  );
}

export default Card
